    <script>
        const Toast = Swal.mixin({
            toast: true,
            position: 'top-right',
            iconColor: 'white',
            customClass: {
                popup: 'colored-toast'
            },
            showConfirmButton: false,
            timer: 2000,
            timerProgressBar: true
        })
        @if(\Illuminate\Support\Facades\Session::has('success'))
        let message = "{{ \Illuminate\Support\Facades\Session::get('success') }}"
        Toast.fire({
            icon: 'success',
            title: message
        })
        @endif
        @if(\Illuminate\Support\Facades\Session::has('error'))
        let message = "{{ \Illuminate\Support\Facades\Session::get('error') }}"
        Toast.fire({
            icon: 'error',
            title: message,
        })
        @endif
        $.ajax({
            url: "{{ adminRoute('notifications.index') }}",
            method: "get",
            success: (res) => {
                let count = res.length;
                $("#notifications a p span").html(count)
            }

        })
    </script>
